	// 月経日追加によるポイント加算
	add_PointForMensesInput : function(){

		var now = GMTDateString(new Date());
		var point_last_updated = this.get_POINT_LAST_UPDATED();

		// 制限回数初期化（一日単位）
		if( now.substr(0,10) != point_last_updated.substr(0,10)){
			this.reset_PLAY_COUNT_DAILY();
		}
		// 制限回数初期化（月経周期単位）
		this.reset_PLAY_COUNT_MENSES();

		// 制限回数取得
		var tundere_play_menses_input = this.get_TUNDERE_PLAY_MENSES_INPUT();
		if(!!!tundere_play_menses_input) tundere_play_menses_input = 0;

		// ポイント
		var tundere_point = this.get_TUNDERE_POINT();
		if(!!!tundere_point) tundere_point = 0;
		var pointup_flg = false;

		// ツンデレポイント加算
		var MAX_TSUNDERE_PLAY_MENSES_INPUT = 1;
		if( tundere_play_menses_input < MAX_TSUNDERE_PLAY_MENSES_INPUT ){
			tundere_point += 5;
			tundere_play_menses_input += 1;
			this.set_TUNDERE_POINT(tundere_point);
			this.set_TUNDERE_PLAY_MENSES_INPUT(tundere_play_menses_input);
			pointup_flg = true;
		}

		// 日時更新
		if(!!pointup_flg) this.set_POINT_LAST_UPDATED(now);

		return true;
	},

	// 制限回数初期化（月経周期単位）
	reset_PLAY_COUNT_MENSES : function(){
		// ファンクション、シーズンポイント
		this.set_FUNCTION_PLAY_SELFCHECK(0);
		this.set_SEASON_PLAY_SELFCHECK(0);
		// ツンデレポイント
		this.set_TUNDERE_PLAY_MIMAMORI_CHECK(0);
		this.set_TUNDERE_PLAY_SELFCHECK_ACTION(0);
		this.set_TUNDERE_PLAY_SELFCHECK_NOT_ACTION(0);
		this.set_TUNDERE_PLAY_MENSES_INPUT(0);
		return true;
	},

