	// 体温追加によるポイント加算
	add_PointForTemperatureInput : function(){

		var now = GMTDateString(new Date());
		var point_last_updated = this.get_POINT_LAST_UPDATED();

		// 制限回数初期化
		if( now.substr(0,10) != point_last_updated.substr(0,10)){
			this.reset_PLAY_COUNT_DAILY();
		}

		// 制限回数取得
		var function_play_temperature      = this.get_FUNCTION_PLAY_TEMPERATURE();
		var season_play_temperature        = this.get_SEASON_PLAY_TEMPERATURE();
		var tundere_play_temperature_input = this.get_TUNDERE_PLAY_TEMPERATURE_INPUT();
		if(!!!function_play_temperature)		function_play_temperature = 0;
		if(!!!season_play_temperature)			season_play_temperature = 0;
		if(!!!tundere_play_temperature_input)	tundere_play_temperature_input = 0;

		// ポイント
		var function_point_temperature	= this.get_FUNCTION_POINT_TEMPERATURE();
		var season_point				= this.get_SEASON_POINT_TERM();
		var tundere_point				= this.get_TUNDERE_POINT();
		if(!!!function_point_temperature)	function_point_temperature = 0;
		if(!!!season_point)					season_point = 0;
		if(!!!tundere_point)				tundere_point = 0;
		var pointup_flg = false;

		// ファンクションポイント加算
		var MAX_FUNCTION_PLAY_TEMPERATURE = 1;
		if( function_play_temperature < MAX_FUNCTION_PLAY_TEMPERATURE ){
			function_point_temperature += 2;
			function_play_temperature  += 1;
			this.set_FUNCTION_POINT_TEMPERATURE(function_point_temperature);
			this.set_FUNCTION_PLAY_TEMPERATURE(function_play_temperature);
			pointup_flg = true;
		}

		// シーズンポイント加算
		var MAX_SEASON_PLAY_TEMPERATURE = MAX_FUNCTION_PLAY_TEMPERATURE;
		if( season_play_temperature < MAX_SEASON_PLAY_TEMPERATURE ){
			season_point += 2;
			season_play_temperature += 1;
			this.set_SEASON_POINT_TERM(season_point);
			this.set_SEASON_PLAY_TEMPERATURE(season_play_temperature);
			pointup_flg = true;
		}

		// ツンデレポイント加算
		var MAX_TSUNDERE_PLAY_TEMPERATURE_INPUT = 1;
		if( tundere_play_temperature_input < MAX_TSUNDERE_PLAY_TEMPERATURE_INPUT ){
			tundere_point += 5;
			tundere_play_temperature_input += 1;
			this.set_TUNDERE_POINT(tundere_point);
			this.set_TUNDERE_PLAY_TEMPERATURE_INPUT(tundere_play_temperature_input);
			pointup_flg = true;
		}

		// 日時更新
		if(!!pointup_flg) this.set_POINT_LAST_UPDATED(now);

		return true;
	},

