	// 各期の日数(7期):SEASON_DAYS
	// 引数：json（{"ranhou1_zen_days": int,
	//				"ranhou1_kou_days": int,
	//				"ranhou2_zen_days": int,
	//				"ranhou2_kou_days": int,
	//				"outai1_zen_days": int,
	//				"outai2_zen_days": int,
	//				"outai2_kou_days": int }）
	get_SEASON_DAYS : function(){
		return JSON.parse(localStorage.getItem("SEASON_DAYS"));
	},

	set_SEASON_DAYS : function(rz1, rk1, rz2, rk2, oz1, oz2, ok2){
		var json = {"ranhou1_zen_days": rz1,
					"ranhou1_kou_days": rk1,
					"ranhou2_zen_days": rz2,
					"ranhou2_kou_days": rk2,
					"outai1_zen_days" : oz1,
					"outai2_zen_days" : oz2,
					"outai2_kou_days" : ok2 };
		localStorage.setItem("SEASON_DAYS", JSON.stringify(json));

		//TERM、TERM4に追加
		var lastMensesDate = Math.floor( new Date( this.get_LAST_MENSES_DATE() ).getTime() / ( 24 * 60 * 60 * 1000 ) );
		var today = Math.floor( new Date().getTime() / ( 24 * 60 * 60 * 1000 ) );

		//4分割
		var termRefresh = rz1 + rk1;
		var termHappy = rz2 + rk2 + termRefresh;
		var termRelax = oz1 + termHappy;
		var termSlow = oz2 + oz2 + termRelax;

		//7分割
		var termRefresh1 = rz1;
		var termRefresh2 = rk1 + termRefresh1;
		var termHappy1 = rz2 + termRefresh2;
		var termHappy2 = rk2 + termHappy1;
		var termRelax1 = oz1 + termHappy2;
		var termSlow1 = oz2 + termRelax1;
		var termSlow2 = oz2 + termSlow1;

		var numTerm4, numTerm7;

		//4分割の期判定
		if ( lastMensesDate + termRefresh - today >= 0 ){
			numTerm4 = 1;
		} else if ( lastMensesDate + termHappy - today >= 0 ){
			numTerm4 = 2;
		} else if ( lastMensesDate + termRelax - today >= 0 ){
			numTerm4 = 3;
		} else if ( lastMensesDate + termSlow - today >= 0 ){
			numTerm4 = 4;
		}

		//7分割の期判定
		if ( lastMensesDate + termRefresh1 - today >= 0 ){
			numTerm7 = 1;
		} else if ( lastMensesDate + termRefresh2 - today >= 0 ){
			numTerm7 = 2;
		} else if ( lastMensesDate + termHappy1 - today >= 0 ){
			numTerm7 = 3;
		} else if ( lastMensesDate + termHappy2 - today >= 0 ){
			numTerm7 = 4;
		} else if ( lastMensesDate + termRelax1 - today >= 0 ){
			numTerm7 = 5;
		} else if ( lastMensesDate + termSlow1 - today >= 0 ){
			numTerm7 = 6;
		} else if ( lastMensesDate + termSlow2 - today >= 0 ){
			numTerm7 = 7;
		} else {
			numTerm7 = 0;
		}
		this.set_TERM4( numTerm4 );
		this.set_TERM( numTerm7 );

		return true;
	},

