	// シールリスト:SEAL_LIST
	// 引数：date(yyyymm or yyyymmdd)
	get_SEAL_LIST : function( date ){
		var ym = "", d = "";
		var list = JSON.parse(localStorage.getItem("SEAL_LIST"));
		if( !!!list ) return null;
		if( !!date ){
			var tmp = date.split("-");
			if( date.length == 10){
				ym = tmp[0] + '' + tmp[1];
				d = tmp[2];
				if(!!list[ym])	return list[ym][d];
				else return null;
			} else if (date.length == 7){
				ym = tmp[0] + '' + tmp[1];
				return list[ym];
			}
		} else {
			return list;
		}
	},

