	set_SEAL : function( date , posID , sealID ){	// yyyy-mm-dd
		if( date.length != 10 ) return false;
		var tmp = date.split("-");
		var ym = tmp[0] + '' + tmp[1];
		var d  = tmp[2];

		var list = this.get_SEAL_LIST();					// 全件取得
		var data = this.get_SEAL_LIST_YMD(date);			// 該当日

		if(!!!data)	data = ["","","","","","","","",""];	// データがない場合は初期化
		data[posID] = sealID;

		if(!!!list[ym])	list[ym] = {};	// 年月のデータがない場合は初期化
		list[ym][d] = data;

		var ret  = this.set_SEAL_LIST(list);
		var ret2 = this.set_SEAL_HISTORY(date , posID , sealID);
		return ret;
	},

	delete_SEAL : function( date , posID ){	// yyyy-mm-dd
		if( date.length != 10 ) return false;
		var tmp = date.split("-");
		var ym = tmp[0] + '' + tmp[1];
		var d  = tmp[2];

		var list = this.get_SEAL_LIST();					// 全件取得

		if(!!list[ym][d]) list[ym][d][posID] = "";
		if(!!!list[ym][d].join("")) delete list[ym][d];

		var ret = localStorage.setItem("SEAL_LIST", JSON.stringify(list));
		var ret2 = this.delete_SEAL_HISTORY(date, posID)
		return ret;
	},

	get_SEAL_HISTORY : function(){
		return JSON.parse(localStorage.getItem("SEAL_HISTORY"));
	},

	set_SEAL_HISTORY : function( date, posID, sealID ){
		var list = this.get_SEAL_HISTORY();	// {"history": [{id", "posID", "sealID", "date"}] };
		var data = { "date": date, "posID": posID, "sealID": sealID, "selected_date": GMTDateString(new Date()) };
		if(!!!list)	list = {"history": []};
		list.history.push(data);
		localStorage.setItem("SEAL_HISTORY", JSON.stringify(list));
		return true;
	},

	delete_SEAL_HISTORY : function( date, posID ){
		var list = this.get_SEAL_HISTORY();
		var del_flg = false;
		for(var i in list.history){			// 重複チェック
			if(list.history[i].date === date && list.history[i].posID === posID){
				list.history.splice(i, 1);
				del_flg = true;
			}
		}
		if(!!del_flg){
			localStorage.setItem("SEAL_HISTORY", JSON.stringify(list));
		}
		return del_flg;
	},

	get_SEAL_HISTORY_COUNT : function(){
		var list = JSON.parse(localStorage.getItem("SEAL_HISTORY"));
		var calc_data = new Array();

		var sealID, last_update;
		for(var i in list.history){
			sealID = list.history[i].sealID;
			last_update = list.history[i].selected_date
			if(!!calc_data[sealID]){
				calc_data[sealID].count++;
				if(calc_data[sealID].last_update < last_update){
					calc_data[sealID].last_update = last_update;
				}
			} else {	// 初期値
				calc_data[sealID] = {"sealID": sealID, "count": 1, "last_update": last_update};
			}
		}

		// 使用頻度の多い順にソート
		calc_data.sort(
			function(a,b){
				var aID   = a["sealID"];
				var aCnt  = a["count"];
				var aDate = a["last_update"];
				var bID   = b["sealID"];
				var bCnt  = b["count"];
				var bDate = b["last_update"];

				if( aCnt > bCnt ) return -1;
				if( aCnt < bCnt ) return 1;

				if(aDate > bDate) return -1;
				if(aDate < bDate) return 1;

				if(aID > bID) return 1;
				if(aID < bID) return -1;

				return 0;
			}
		);

		return calc_data;
	},


	// シール追加によるポイント加算
	add_PointForSealSet : function(){

		var now = GMTDateString(new Date());
		var point_last_updated = this.get_POINT_LAST_UPDATED();
		var last_set_date = this.get_SEAL_LAST_SET();
		var pointup_flg = false;

		// 制限回数
		var function_play_seal = this.get_FUNCTION_PLAY_SEAL();
		var season_play_seal = this.get_SEASON_PLAY_SEAL();
		var tundere_play_seal_set = this.get_TUNDERE_PLAY_SEAL_SET();
		if(!!!function_play_seal)		function_play_seal = 0;
		if(!!!season_play_seal)			season_play_seal = 0;
		if(!!!tundere_play_seal_set)	tundere_play_seal_set = 0;

		// ポイント
		var function_point_seal = this.get_FUNCTION_POINT_SEAL();
		var season_point = this.get_SEASON_POINT_TERM();
		var tundere_point = this.get_TUNDERE_POINT();
		if(!!!function_point_seal)	function_point_seal = 0;
		if(!!!season_point)			season_point = 0;
		if(!!!tundere_point)		tundere_point = 0;

/** ポイント最終更新日をチェックして初期化するよう変更
		// 制限回数初期化
		if( !!last_set_date && now.substr(0,10) != last_set_date.substr(0,10) ){
			function_play_seal = 0;
			season_play_seal = 0;
			tundere_play_seal_set = 0;
			this.set_FUNCTION_PLAY_SEAL(0);
			this.set_SEASON_PLAY_SEAL(0);
			this.set_TUNDERE_PLAY_SEAL_SET(0);
		}
**/
		// 制限回数初期化
		if( now.substr(0,10) != point_last_updated.substr(0,10)){
			this.reset_PLAY_COUNT_DAILY();
		}

		// ファンクションポイント加算
		var MAX_FUNCTION_PLAY_SEAL = 9;
		if( function_play_seal < MAX_FUNCTION_PLAY_SEAL ){
			function_point_seal += 2;
			function_play_seal  += 1;
			this.set_FUNCTION_POINT_SEAL(function_point_seal);
			this.set_FUNCTION_PLAY_SEAL(function_play_seal);
			pointup_flg = true;
		}

		// シーズンポイント加算
		var MAX_SEASON_PLAY_SEAL = 9;
		if( season_play_seal < MAX_SEASON_PLAY_SEAL ){
			season_point += 2;
			season_play_seal += 1;
			this.set_SEASON_POINT_TERM(season_point);
			this.set_SEASON_PLAY_SEAL(function_play_seal);
			pointup_flg = true;
		}

		// ツンデレポイント加算
		var MAX_TSUNDERE_PLAY_SEAL_SET = 5;
		if( tundere_play_seal_set < MAX_TSUNDERE_PLAY_SEAL_SET ){
			tundere_point += 1;
			tundere_play_seal_set += 1;
			this.set_TUNDERE_POINT(tundere_point);
			this.set_TUNDERE_PLAY_SEAL_SET(tundere_play_seal_set);
			pointup_flg = true;
		}

		// 日時更新
		if(!!pointup_flg) this.set_POINT_LAST_UPDATED(now);
		this.set_SEAL_LAST_SET(now);

		return true;
	},

