	/*
	 * get_VOICE
	 *
	 * WebSQLからカラダボイスのデータを抽出
	 * 引数：priority(優先順位の条件)、data（抽出条件）、callback（コールバック関数）
	 *　戻り値：なし
	 */

	get_VOICE : function ( priority , data , callback ){
		var db = html5sql;
		var sql_str;
		switch ( priority ){
			//優先順位1
			case ( 1 ) :
				// console.log("■■■■■■■get_voice" , priority , data );
				sql_str = "SELECT * FROM voice WHERE public_flag = ? AND cycle = ? AND ( (season_start <= ? AND season_end >= ?) OR ( season_start <= ? AND season_end < season_start) OR (season_end >= ? AND season_end <  season_start) ) AND TIME =? AND weather =? ;";
				break;
			//優先順位2（天気ALL）
			case ( 2 ) :
				data.splice( data.length - 1 , 1 );
				// console.log("■■■■■■■get_voice 天気ALL " , priority , data );
				sql_str = "SELECT * FROM voice WHERE public_flag = ? AND cycle =? AND ( (season_start <=  ? AND season_end >=  ?) OR (season_start <=  ? AND season_end <  season_start) OR (season_end >= ? AND season_end <  season_start) ) AND TIME =? ;";
				break;
			//優先順位3（天気ALL、時間帯ALL）
			case ( 3 ) :
				data.splice( data.length - 1 , 1 );
				// console.log("■■■■■■■get_voice 天気ALL、時間帯ALL " , priority , data );
				sql_str = "SELECT * FROM voice WHERE public_flag = ? AND cycle =? AND ( (season_start <=  ? AND season_end >=  ?) OR (season_start <=  ? AND season_end <  season_start) OR (season_end >= ? AND season_end <  season_start) ) ;";
				break;
			//優先順位4（天気ALL、時間帯ALL、時期ALL）
			case ( 4 ) :
				// console.log("■■■■■■■get_voice 天気ALL、時間帯ALL、時期ALL " , priority , data );
				sql_str = "SELECT * FROM voice WHERE public_flag = ? AND cycle =? AND ( (season_start <=  ? AND season_end >=  ?) OR (season_start <=  ? AND season_end <  season_start) OR (season_end >= ? AND season_end <  season_start) OR (season_start =  '0000' AND season_end = '0000') ) ;";
				break;
			//優先順位5（天気ALL、時間帯ALL、時期ALL、周期ALL）
			case ( 5 ) :
				data.splice( 1 , 1 );
				// console.log("■■■■■■■get_voice 天気ALL、時間帯ALL、時期ALL、周期ALL " , priority , data );
				sql_str = "SELECT * FROM voice WHERE public_flag = ? AND ( (season_start <=  ? AND season_end >=  ?) OR (season_start <=  ? AND season_end <  season_start)OR (season_end >= ? AND season_end <  season_start) OR (season_start =  '0000' AND season_end = '0000') ) ;";
				break;
			case ( 6 ) :
				// console.log("■■■■■■■get_voice 同一テーマクリア " , priority , data );
				sql_str = "SELECT * FROM voice WHERE public_flag = ? AND ( (season_start <=  ? AND season_end >=  ?) OR (season_start <=  ? AND season_end <  season_start) OR (season_end >= ? AND season_end <  season_start) OR (season_start =  '0000' AND season_end = '0000') ) ;";
				break;
		}
		//console.log( 'data' , data)


		db.process([{
			//sql :"SELECT * FROM voice WHERE public_flag =? AND cycle =? AND TIME =? AND weather =?;",

			sql : sql_str,
			//sql :" SELECT * FROM voice WHERE public_flag = ? AND cycle =? AND TIME =? AND weather  ? AND ( (season_start <=  ? AND season_end >=  ?) OR (season_start <=  ? AND season_end <  season_start) OR (season_start =  '0000' AND season_end = '0000') ); ",

			//sql : 'SELECT * FROM voice WHERE season_start <= ? AND season_end >= ?;',
			data : data,
			success : function( transaction, results, list ){
				//console.log( 'get_Voice_comp' , transaction, results, list );
				//callback.call(this,transaction,results,list);
				callback( list );
			}
		}],
		function(){
			//console.log("get ok");
		},
		function(error, problemSQLStatement){
			//console.log("get ng");
			throw new Error("Error:"+error.message+" Processing SQL Statement "+problemSQLStatement);
		})
	},


	//VOICE_DO_ARRAY　（「やる」を選択したボイス） 引数のボイスがすでに「やる」を選択されたボイスでなければ-1を返す
	check_VOICE_DO_ARRAY : function( num ){
		if ( localStorage.getItem( "VOICE_DO_ARRAY" ) != undefined ){
			var doArray = localStorage.getItem( "VOICE_DO_ARRAY" ).split(",");
			var result = _.indexOf( doArray , num );
			return result;
		} else {
			return -1;
		}
	},
	//追加
	set_VOICE_DO_ARRAY : function( num ){
		var doArray = [];
		var doArray2 = localStorage.getItem("VOICE_DO_ARRAY");
		if( doArray2 == null ){
			doArray[0] = num;
		}else{
			doArray = doArray.concat( doArray2.split(",") );
			doArray.push( num );
		}
		localStorage.setItem( "VOICE_DO_ARRAY", doArray.toString() );
		return true;
	},
	//クリア
	remove_VOICE_DO_ARRAY : function(){
		localStorage.removeItem( "VOICE_DO_ARRAY" );
		return true;
	},

